const Header = (props) => {
  return(
    <h1>{props.course}</h1>
  )
}
const Part = (props) =>{
  return(
    <p>{props.part} {props.exercise}</p>
  )
}
const Content = (props) =>{
  const [part1, part2, part3] = props
  console.log(part1)
  return(
  <div>
    <Part part={part1.name} exercise={part1.exercises1}/>
    <Part part={part2.name} exercise={part2.exercises2}/> 
    <Part part={part3.name} exercise={part3.exercises3}/> 
  </div>
  )
}
const Total = (props) =>{
  return(
   <p>Number of exercises {props.exercises1 + props.exercises2 
     +props.exercises3} </p>
  )
}
const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]
  return(
    <div>
      <Header course={course} />
      <Content parts={parts} />
      <Total exercises1={parts[0].exercises} exercises2={parts[1].exercises}
      exercises3={parts[2].exercises} />
    </div>
  )
}

export default App;
