import { useState } from 'react'

const ShowHeader = () =>{
  return(
    <>
      <h1> give feedback </h1>
      <br/>
    </>
  )
}
const Button = (props) =>{
 return(<button onClick={props.handleClick}> 
      {props.text} 
    </button>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);

  const handleClick = (setFunction, setValue) => {
    return setFunction(setValue)
  }


  return(
    <div>
      <ShowHeader />
      <Button onClick={handleClick, good+1} text="good" />
      <Button onClick={handleClick, neutral+1} text="neutral" />
      <Button onClick={handleClick, bad+1} text="bad" />
    </div>
  )
}

export default App
